const SingleBook =({handleSelected,book,selected})=> {
  // state = {
  //     selected: false
  // }


    return (
      <>
        <Card
          onClick={() => handleSelected(book.asin)}
          style={{
            border:
              selected === book.asin
                ? '3px solid red'
                : 'none',
          }}
        >
          <Card.Img variant='top' src={book.img} className='imageBook'/>
          <Card.Body>
            <Card.Title className="text-truncate" style={{ color: 'black' }}>
              {book.title}
            </Card.Title>
          </Card.Body>
        </Card>
        {/* {this.props.selected === this.props.book.asin && (
          <CommentArea asin={this.props.book.asin} />
        )} */}
      </>
    )

}

export default SingleBook
